import React, { useState } from "react";
import { useSelector } from "react-redux";
import "./user.css";

const UserPage = () => {
  const configuredFields = useSelector((state) => state.admin.config.fields);
  const [formData, setFormData] = useState({});

  const occupationOptions = [
    { value: 'student', label: 'ðŸ‘¤ Student' },
    { value: 'business', label: 'ðŸ‘¤ Business' },
    { value: 'salaried', label: 'ðŸ‘¤ Salaried' },
  ];

  const handleInputChange = (fieldName, value) => {
    setFormData({
      ...formData,
      [fieldName]: value,
    });
  };

  const handleSubmit = () => {
    console.log("Form Data:", formData);
    setFormData({});
  };

  const renderInput = (field) => {
    switch (field.type) {
      case "textbox":
        return (
          <input
            type="text"
            className="input-field"
            onChange={(e) => handleInputChange(field.config.displayName, e.target.value)}
          />
        );
      case "dropdown":
        return (
          <select
            className="input-field"
            onChange={(e) => handleInputChange(field.config.displayName, e.target.value)}
          >
            {field.config.fieldData?.map((option, index) => (
              <option key={index} value={option.value}>
                {option.label}
              </option>
            ))}
          </select>
        );
      case "datepicker":
        return (
          <input
            type="date"
            className="input-field"
            onChange={(e) => handleInputChange(field.config.displayName, e.target.value)}
          />
        );
      default:
        return null;
    }
  };

  return (
    <div className="user-page-container">
      <h2>User Page</h2>
      <form>
        <div className="form-group">
          <div className="input-container">
            <select
              className="input-field"
              onChange={(e) => handleInputChange("occupation", e.target.value)}
            >
              {occupationOptions.map((option, index) => (
                <option key={index} value={option.value}>
                  {option.label}
                </option>
              ))}
            </select>
            <label className="input-label">Occupation</label>
          </div>
        </div>
        {configuredFields?.map((field, index) => (
          <div key={index} className="form-group">
            <div className="input-container">
              {renderInput(field)}
              <label className="input-label">{field.config.displayName}</label>
            </div>
          </div>
        ))}
        <button type="button" className="btn btn-primary" onClick={handleSubmit}>
          Submit
        </button>
      </form>
    </div>
  );
};

export default UserPage;
